css:
.masterclass{
    border: 1px solid black;
    border-top: 0;
    border-right: 0;
}

.toptable{
    width: 100%;
}

.toptable table{
    width: 100%;
    border-top: 1px solid black;
    font-size: 12px;
}

.tf{
    width:25%;
}

.toptable td{
    border-bottom: 1px solid black;
    border-right: 1px solid black;
    color: black;
}

.documentreq{
    overflow:hidden;
    border-right:1px solid black
}

.documentreq td{
    border-bottom: 0 !important;
    border-right: 0!important;
}

.item td{
    border-bottom: 1px solid black;
    border-right: 1px solid black;
    color: black;
}

.item tr{
    border-left: 1px solid black;
}

.item table{
    width:100%;
    border-top: 1px solid black;
}
