css:
.main{
    border: 1px solid black;
    width:100%;
}

.heading img{
    float:left;
    width:200px;
    height:80px;
    margin-left:50px;
}

.heading h1{
    float:right;
    width:60%;
    font-weight:bold;
}

.heading{
    overflow:hidden;
    border-bottom:10px solid #1f497d;
}

.address{
    border-bottom:10px solid #1f497d;
}

.address td{
    line-height:4mm !important;
    border-right:1px solid black;
    border-bottom:1px solid black;
}

.address table {
    color:black;
    width:100%;
}

.redtext{
    color:red;
    text-align: center;
    font-weight:bold;
}

.mainaddr{
    width:100%;
    overflow:hidden;
}

.shipaddr{
    float:left;
    margin-left:10px;
}

.invoiceaddr{
    float:right;
    margin-right:10px;
}

.itemtable{
    width:100%;
}

.itemtable table{
    color:black !important;
    width:100%;
    font-size:15px;
    border-bottom:10px solid #c0504d;
}

.itemtable th{
    color:white !important;
    font-weight:bold;
    background-color:#c0504d;
    border-right:1px solid black;
    border-bottom:1px solid black;
}

.itemtable td{
    color:black !important;
    border-right:1px solid black;
    border-bottom:1px solid black;
}
